import React, { Component } from "react";

import { withRouter } from "react-router-dom/cjs/react-router-dom.min";
import { initializeApp } from "firebase/app";

import {
  getAuth,
  signInWithEmailAndPassword,
  GoogleAuthProvider,
  signInWithPopup,
} from "firebase/auth";

const firebaseConfig = {
  apiKey: "AIzaSyAqY2k6c_f6HAUBzC9IvViWoDMLoAcjaSo",
  authDomain: "praveenkumarpatini-ac74d.firebaseapp.com",
  projectId: "praveenkumarpatini-ac74d",
  storageBucket: "praveenkumarpatini-ac74d.appspot.com",
  messagingSenderId: "629125667796",
  appId: "1:629125667796:web:65d89685bfbdc01ea6d441",
};
const app = initializeApp(firebaseConfig);

class Login extends Component {
  state = { email: "", password: "" };

  handleEmail = (e) => {
    this.setState({ email: e.target.value });
  };
  handlePassword = (e) => {
    this.setState({ password: e.target.value });
  };
  render() {
    return (
      <div>
        {/* <h1>Login</h1>

        <input
          type="email"
          placeholder="Email"

        />
        <input
          type="password"
          placeholder="password"


        />
        <div>
          <button>Login</button>
          <button>Sign up With Google</button>
        </div> */}
        <div class="ui placeholder segment">
          <div class="ui two column very relaxed stackable grid">
            <div class="column">
              <div class="ui form">
                <div class="field">
                  <label>Username</label>
                  <div class="ui left icon input">
                    <input
                      type="text"
                      placeholder="email"
                      value={this.state.email}
                      onChange={(e) => this.handleEmail(e)}
                    />
                    <i class="user icon"></i>
                  </div>
                </div>
                <div class="field">
                  <label>Password</label>
                  <div class="ui left icon input">
                    <input
                      type="password"
                      placeholder="password"
                      value={this.state.password}
                      onChange={(e) => this.handlePassword(e)}
                    />
                    <i class="lock icon"></i>
                  </div>
                </div>
                <div class="ui blue submit button">Login</div>
              </div>
            </div>
            <div class="middle aligned column">
              <div class="ui big button">
                <i class="signup icon"></i>
                Register
              </div>
              <div class="ui big button">
                <i class="signup icon"></i>
                Sign Up With Google
              </div>
            </div>
          </div>
          <div class="ui vertical divider">Or</div>
        </div>
      </div>
    );
  }
}

export default Login;
